export function pageReader(frameContent) {
  let frameHTML = frameContent.contentWindow.document
  let divsAndSections = frameHTML.querySelectorAll('body > div[id], body > section[id]') //sadece id attribute barindirian ilk elemanlar secildi. sonucta id unique olmali.
  let header = frameHTML.querySelector('header')
  // header elemanini da eklemek istedim cunku neden olmasin
  header.id = 'he-header'
  let topLevelElements = [header, ...divsAndSections]

  //frame icine style ekleme
  let frameStyle = document.createElement('style')
  frameStyle.innerHTML = `
.signalize{
overflow:hidden;
animation: signalize 5s infinite;
}

  @keyframes signalize {
    0% {
      scale: 1;
    }
    50% {
      scale: 1.05;
    }
      100%{
      scale:1;
      }
  }
  `
  frameHTML.head.append(frameStyle)
  //frame icine style ekleme

  let idList = []

  topLevelElements.forEach((el) => {
    idList.push(el.id)
  })

  // return topLevelElements //html collection olarak donen array. Direkt olarak html elemanlarini barindiriyor
  return idList
}

export function styleSelected(frameContent, selectedId) {
  let frameHTML = frameContent.contentWindow.document

  let previousIframeEl = frameHTML.querySelector('.signalize') || null
  console.log(previousIframeEl)

  let selectedIframeEl = frameHTML.querySelector('#' + selectedId)

  if (previousIframeEl) {
    console.log('burada')
    previousIframeEl.classList.remove('signalize')
  }

  selectedIframeEl.classList.add('signalize')
}
