/**
 * Usage:
 * import { loadGoogleCharts } from 'vue-google-charts'
 *
 * loadGoogleCharts('current', { packages: ['corechart', 'map'] })
 *  .then(google => {
 *    const chart = new google.visualization.Map ...
 *  })
 */

import type {
  GoogleViz,
  GoogleChartLoader,
  GoogleChartVersion,
  GoogleChartLoaderOptions,
} from '../types';

const chartsScriptUrl = 'https://www.gstatic.com/charts/loader.js';

let chartsLoaderPromise: null | Promise<GoogleChartLoader> = null;

const loadedPackages = new Map();

export function getChartsLoader(): Promise<GoogleChartLoader> {
  // If already included in the page:
  if (window.google !== undefined) {
    return Promise.resolve(window.google.charts);
  }

  if (chartsLoaderPromise === null) {
    chartsLoaderPromise = new Promise(resolve => {
      // Find script tag with same src in DOM.
      const foundScript = document.querySelector<HTMLScriptElement>(
        `script[src="${chartsScriptUrl}"]`
      );

      // Create or get existed tag.
      const script = foundScript || document.createElement('script');

      // Set src if no script was found.
      if (!foundScript) {
        script.src = chartsScriptUrl;
        script.type = 'text/javascript';

        document.head.append(script);
      }

      script.onload = () => {
        if (window.google !== undefined) {
          resolve(window.google.charts);
        }
      };
    });
  }

  return chartsLoaderPromise;
}

/**
 * Function to load Google Charts JS API.
 * @param version - Chart version to load.
 * @param packages - Packages to load.
 * @param language - Languages to load.
 * @param mapsApiKey - Google Maps api key.
 * @returns
 */
export async function loadGoogleCharts(
  version: GoogleChartVersion = 'current',
  {
    packages = ['corechart', 'controls'],
    language = 'en',
    mapsApiKey,
  }: GoogleChartLoaderOptions
): Promise<GoogleViz | undefined> {
  const loader = await getChartsLoader();

  const settingsKey = `${version}_${packages.join('_')}_${language}`;

  if (loadedPackages.has(settingsKey)) return loadedPackages.get(settingsKey);

  const loaderPromise: Promise<GoogleViz | undefined> = new Promise(resolve => {
    loader.load(version, {
      packages,
      language,
      mapsApiKey,
    });

    loader.setOnLoadCallback(() => resolve(window.google));
  });

  loadedPackages.set(settingsKey, loaderPromise);

  return loaderPromise;
}
import {
  GoogleChartWrapper,
  GoogleChartWrapperChartType,
  GoogleDataTable,
  GoogleDataView,
  GoogleViz,
  GoogleVizEventName,
  GoogleVizEvents,
} from './types';

export interface ICreateChartFunction {
  (
    el: HTMLElement,
    google: GoogleViz,
    type: GoogleChartWrapperChartType
  ): GoogleChartWrapper;
}

export function getValidChartData(
  chartsLib: null | GoogleViz,
  data: unknown[][] | GoogleDataTable | Record<string, any> | null,
  isFirstRowLabels?: boolean
): GoogleDataTable | GoogleDataView | null {
  if (chartsLib !== null && data instanceof chartsLib.visualization.DataTable) {
    return data;
  }

  if (chartsLib !== null && data instanceof chartsLib.visualization.DataView) {
    return data;
  }

  if (chartsLib !== null && Array.isArray(data)) {
    return chartsLib.visualization.arrayToDataTable(data, isFirstRowLabels);
  }

  if (chartsLib !== null && data !== null && typeof data === 'object') {
    return new chartsLib.visualization.DataTable(data);
  }

  return null;
}

export function createChartObject(
  chartsLib: GoogleViz | null,
  chartObject: GoogleChartWrapper | null,
  chartEl: HTMLElement | null,
  chartType: GoogleChartWrapperChartType,
  chartEvents: GoogleVizEvents | null,
  createChartFunction?: ICreateChartFunction
): GoogleChartWrapper | null {
  const createChart: ICreateChartFunction = (
    el: HTMLElement,
    google: GoogleViz,
    type: GoogleChartWrapperChartType
  ): GoogleChartWrapper => {
    if (type === undefined) {
      throw new Error('please, provide chart type property');
    }

    return new google.visualization[type](el);
  };

  if (chartsLib === null) {
    throw new Error('please, provide charts lib property');
  }

  if (chartEl === null) {
    throw new Error('please, provide chart element property');
  }

  const fn = createChartFunction || createChart;

  chartObject = fn(chartEl, chartsLib, chartType);

  attachListeners(chartsLib, chartObject, chartEvents);

  return chartObject;
}

function attachListeners(
  chartsLib: null | GoogleViz,
  chartObject: GoogleChartWrapper | null,
  chartEvents: GoogleVizEvents | null
): void {
  if (chartEvents === null) {
    return;
  }

  for (const [event, listener] of Object.entries(chartEvents)) {
    if (chartsLib !== null && chartObject !== null) {
      chartsLib.visualization.events.addListener(
        chartObject,
        event as GoogleVizEventName,
        listener as (chartWrapper: GoogleChartWrapper) => any
      );
    }
  }
}
export interface DebouncedFunction<
  Args extends any[],
  F extends (...args: Args) => any
> {
  (this: ThisParameterType<F>, ...args: Args & Parameters<F>): void;
}

export function debounce<Args extends any[], F extends (...args: Args) => any>(
  func: F,
  waitMilliseconds = 50
): DebouncedFunction<Args, F> {
  let timeoutId: ReturnType<typeof setTimeout> | undefined;
  function nextInvokeTimeout() {
    return waitMilliseconds;
  }

  const debouncedFunction = function (
    this: ThisParameterType<F>,
    ...args: Parameters<F>
  ) {
    const context = this;

    const invokeFunction = function () {
      timeoutId = undefined;

      func.apply(context, args);
    };

    if (timeoutId !== undefined) {
      clearTimeout(timeoutId);
    }

    timeoutId = setTimeout(invokeFunction, nextInvokeTimeout());
  };

  return debouncedFunction;
}
import {
  defineComponent,
  ref,
  h,
  onMounted,
  onBeforeUnmount,
  watch,
  PropType,
} from 'vue';

import {
  GoogleViz,
  GoogleChartWrapper,
  GoogleChartWrapperChartType,
  GoogleChartOptions,
  GoogleChartVersion,
  GoogleChartLoaderOptions,
  GoogleVizEvents,
  GoogleDataTable,
} from '../types';

import {
  createChartObject,
  getValidChartData,
  ICreateChartFunction,
} from '../utils';

import { loadGoogleCharts } from '../lib/google-charts-loader';
import { debounce } from '../lib/debounce';

let chartsLib: null | GoogleViz = null;

export const GChart = defineComponent({
  name: 'GChart',
  props: {
    type: {
      type: String as PropType<GoogleChartWrapperChartType>,
      required: true,
    },
    data: {
      type: [Array, Object, null] as PropType<
        unknown[][] | GoogleDataTable | Record<string, any> | null
      >,
      default: () => [],
    },
    isFirstRowLabels: {
      type: Boolean,
      default: false,
    },
    options: {
      type: Object as PropType<GoogleChartOptions>,
      default: () => ({}),
    },
    version: {
      type: String as PropType<GoogleChartVersion>,
      default: 'current',
    },
    settings: {
      type: Object as PropType<GoogleChartLoaderOptions>,
      default: () => ({
        packages: ['corechart', 'table'],
      }),
    },
    events: {
      type: Object as PropType<GoogleVizEvents | null>,
      default: null,
    },
    createChart: {
      type: Function as PropType<ICreateChartFunction>,
      default: undefined,
    },
    resizeDebounce: {
      type: Number,
      default: 200,
    },
  },
  setup(props, { emit }) {
    const chartObject = ref<GoogleChartWrapper | null>(null);
    const chartEl = ref<HTMLElement | null>(null);

    function drawChart(): void {
      if (chartsLib === null || chartObject.value === null) {
        return;
      }

      const data = getValidChartData(
        chartsLib,
        props.data,
        props.isFirstRowLabels
      );
      if (data !== null) {
        chartObject.value?.draw(data, props.options);
      }
    }

    watch(
      () => props.data,
      () => drawChart(),
      { deep: true }
    );

    watch(
      () => props.options,
      () => drawChart(),
      { deep: true }
    );

    watch(
      () => props.type,
      () => {
        chartObject.value = createChartObject(
          chartsLib,
          chartObject.value,
          chartEl.value,
          props.type,
          props.events,
          props.createChart
        );

        drawChart();
      }
    );

    onMounted(() => {
      loadGoogleCharts(props.version, props.settings).then(api => {
        if (api !== undefined) {
          chartsLib = api;

          chartObject.value = createChartObject(
            chartsLib,
            chartObject.value,
            chartEl.value,
            props.type,
            props.events,
            props.createChart
          );

          emit('ready', chartObject.value, api);

          drawChart();
        }
      });

      if (props.resizeDebounce > 0) {
        window.addEventListener(
          'resize',
          debounce(drawChart, props.resizeDebounce) as EventListener
        );
      }
    });

    onBeforeUnmount(() => {
      if (
        chartObject.value !== null &&
        typeof chartObject.value.clearChart === 'function'
      ) {
        chartObject.value.clearChart();
      }

      if (props.resizeDebounce > 0) {
        window.removeEventListener(
          'resize',
          debounce(drawChart, props.resizeDebounce) as EventListener
        );
      }
    });

    return () => h('div', { ref: chartEl }, []);
  },
});
import { createApp } from 'vue';

import { loadGoogleCharts } from './lib/google-charts-loader';
import { GChart } from './components/GChart';

const app = createApp({});

// Install the components
export function install() {
  app.component('GChart', GChart);
}

// Expose the components
export { loadGoogleCharts, GChart };

/* -- Plugin definition & Auto-install -- */
/* You shouldn't have to modify the code below */

// Plugin
const plugin = {
  version: process.env.VERSION,
  install,
};

export default plugin;

// Auto-install
let GlobalVue = null;
if (typeof window !== 'undefined') {
  GlobalVue = window.Vue;
}

if (GlobalVue !== null && GlobalVue !== undefined) {
  app.use(plugin);
}
