        /* Re-order items into 3 rows */
        .partner-content:nth-child(3n+1),
        .partner-content-tec:nth-child(3n+1) {
            order: 1;
        }

        .partner-content:nth-child(3n+2),
        .partner-content-tec:nth-child(3n+2) {
            order: 2;
        }

        .partner-content:nth-child(3n),
        .partner-content-tec:nth-child(3n) {
            order: 3;
        }

        .partner-content-tec:last-child {
            order: 3;
        }

        /* Force new columns */
        .partnership::before,
        .partnership::after,
        .partnership-tec::before,
        .partnership-tec::after {
            content: "";
            flex-basis: 100%;
            width: 0;
            order: 2;
        }

        .partner-logo {
            width: 175px;
            margin-bottom: 20px;
        }

        .green,
        .blue {
            cursor: pointer;
        }

        .animated-submenu>.sub-menu {
            border-bottom-left-radius: 30px;
            border-bottom-right-radius: 30px;
        }
        }




        /*
 * Documentation
 */
        .buttons-hover .stk--svg-wrapper {
            opacity: 0;
            transform: translate(-30px, 0);
            transition: all 0.3s linear;
        }

        .buttons-hover:hover .stk--svg-wrapper {
            opacity: 1;
            transform: translate(0);
        }

        /*
 * integration-developer-guide
 */
        .msbtable {
            text-align: center;
            width: 100%;
        }

        .msbtable td,
        .msbtable th {
            border-color: #e5e5e5 !important;
        }

        .msbtable thead th {
            font-size: 11px;
            color: rgb(85, 85, 85);
            word-break: break-all;
            width: 128px;
        }

        .msbtable thead th:nth-child(2n) {
            font-weight: 700;
        }

        .msbtable td:nth-child(2n) {
            background-color: rgb(243, 243, 243);
        }

        .msbtable td:nth-child(2n).checkBox {
            background-color: #3cc490;
        }

        .msbtable td:nth-child(2n+1).checkBox {
            background-color: #3aa077;
        }

        .msbtable tbody th {
            font-size: 14.4px;
            font-weight: 400;
            width: 15%;
            text-align: left;
        }

        .tef {
            font-size: 11px !important;
        }

        .stk--is-open .msbRadius,
        .stk--is-open .msbRadiusbottom {
            background-color: #f6e9d4 !important;
        }

        .msbRadius {

            border-radius: 50px !important;
        }

        .msbRadiusbottom {
            border-bottom-right-radius: 50px;
            border-bottom-left-radius: 50px;
        }

        /*
 * CASE STUDY
 */
        .case-study-slide .slick-arrow::before {
            color: #000 !important;
        }

        /*
 * Time line
 */
        .dots,
        .dots-left {
            position: relative;
            margin-bottom: 24px;
        }

        .dots::after {
            content: "" !important;
            height: 15px;
            width: 15px;
            position: absolute;
            top: 0 !important;
            border-radius: 50%;
            right: -20px !important;
        }

        .dots-left::after {
            content: "" !important;
            height: 15px;
            width: 15px;
            position: absolute;
            top: 0 !important;
            border-radius: 50%;
            left: -20px !important;
        }

        .dots-left2::after {
            content: "" !important;
            height: 15px;
            width: 15px;
            position: absolute;
            top: 0% !important;
            border-radius: 50%;
            left: -20px !important;
        }

        .red::after {
            background-color: #d52028;
        }

        .yellow::after {
            background-color: #f8aa29;
        }

        .white::after {
            background-color: #ffffff;
        }

        .blue::after {
            background-color: #3cc1dd;
        }

